import { useState } from 'react';

export const Join = () => {

    const [ form, setForm ] = useState();


    const formChange = (val, id) => {
        setForm(() => ({...form, [id]: val}));
    }
    return(
        <div>
            <div>
                <h1>Join/Create Meeting</h1>
                <div>
                    <input placeholder="Room id" type="text" onChange={(val) => formChange(val, 'roomId')}/>
                    {/*add a button to generate an ID... <button onClick></button> */}
                </div>
                <div>
                    <input 
                        placeholder="Name" 
                        type="text" 
                        onChange={(val) => formChange(val, 'roomId')}
                        required/>
                </div>
                <Link to={'/chat'}>
                    <button type="submit">Join</button>
                </Link>
            </div>
        </div>
    )
}
